import React from 'react';

interface ItemProps {
    name:string,
    email:string,
    children?:any
  }
const Item = (props: ItemProps) => (
        <div className="Item" >
            <p><b>{props.name}</b>: {props.email}</p>
        </div>
)
export default Item;
